$(document).ready(function(){
    // Este código se ejecuta cuando el documento HTML ha sido completamente cargado
    // Aquí puedes escribir tu código jQuery para interactuar con el DOM
    
    // Por ejemplo, puedes seleccionar un elemento y cambiar su contenido de texto
    $("#miElemento").text("Nuevo texto");
    
    // O puedes agregar una clase a un elemento
    $(".miOtroElemento").addClass("nuevaClase");
});
